/* LIGHT MODE */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 0 0% 99%;

  --foreground: 220 13% 18%;

  --border: 220 9% 90%;

  --card: 0 0% 100%;

  --card-foreground: 220 13% 18%;

  --card-border: 220 9% 93%;

  --sidebar: 220 9% 96%;

  --sidebar-foreground: 220 13% 18%;

  --sidebar-border: 220 9% 89%;

  --sidebar-primary: 210 100% 56%;

  --sidebar-primary-foreground: 0 0% 98%;

  --sidebar-accent: 220 9% 92%;

  --sidebar-accent-foreground: 220 13% 18%;

  --sidebar-ring: 210 100% 56%;

  --popover: 0 0% 97%;

  --popover-foreground: 220 13% 18%;

  --popover-border: 220 9% 88%;

  --primary: 210 100% 56%;

  --primary-foreground: 0 0% 98%;

  --secondary: 220 9% 88%;

  --secondary-foreground: 220 13% 18%;

  --muted: 220 9% 91%;

  --muted-foreground: 220 9% 46%;

  --accent: 220 12% 94%;

  --accent-foreground: 220 13% 18%;

  --destructive: 0 84% 60%;

  --destructive-foreground: 0 0% 98%;

  --input: 220 13% 75%;
  --ring: 210 100% 56%;
  --chart-1: 210 100% 56%;
  --chart-2: 160 84% 39%;
  --chart-3: 200 98% 39%;
  --chart-4: 38 92% 50%;
  --chart-5: 142 76% 36%;

  --font-sans: Inter, system-ui, -apple-system, sans-serif;
  --font-serif: Playfair Display, Georgia, serif;
  --font-mono: JetBrains Mono, Menlo, monospace;
  --radius: .5rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(220 13% 18% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(220 13% 18% / 0.08);
  --shadow-sm: 0px 2px 4px -1px hsl(220 13% 18% / 0.06), 0px 1px 2px -1px hsl(220 13% 18% / 0.08);
  --shadow: 0px 4px 6px -1px hsl(220 13% 18% / 0.08), 0px 2px 4px -2px hsl(220 13% 18% / 0.06);
  --shadow-md: 0px 6px 12px -2px hsl(220 13% 18% / 0.10), 0px 3px 6px -3px hsl(220 13% 18% / 0.08);
  --shadow-lg: 0px 12px 24px -4px hsl(220 13% 18% / 0.12), 0px 6px 12px -6px hsl(220 13% 18% / 0.08);
  --shadow-xl: 0px 20px 40px -8px hsl(220 13% 18% / 0.15), 0px 10px 20px -10px hsl(220 13% 18% / 0.10);
  --shadow-2xl: 0px 32px 64px -12px hsl(220 13% 18% / 0.18);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 220 13% 10%;

  --foreground: 0 0% 98%;

  --border: 220 13% 22%;

  --card: 220 13% 14%;

  --card-foreground: 0 0% 98%;

  --card-border: 220 13% 18%;

  --sidebar: 220 13% 12%;

  --sidebar-foreground: 0 0% 98%;

  --sidebar-border: 220 13% 16%;

  --sidebar-primary: 210 100% 62%;

  --sidebar-primary-foreground: 0 0% 98%;

  --sidebar-accent: 220 13% 16%;

  --sidebar-accent-foreground: 0 0% 98%;

  --sidebar-ring: 210 100% 62%;

  --popover: 220 13% 16%;

  --popover-foreground: 0 0% 98%;

  --popover-border: 220 13% 20%;

  --primary: 210 100% 62%;

  --primary-foreground: 0 0% 98%;

  --secondary: 220 13% 20%;

  --secondary-foreground: 0 0% 98%;

  --muted: 220 13% 18%;

  --muted-foreground: 220 9% 64%;

  --accent: 220 15% 19%;

  --accent-foreground: 0 0% 98%;

  --destructive: 0 84% 60%;

  --destructive-foreground: 0 0% 98%;

  --input: 220 13% 35%;
  --ring: 210 100% 62%;
  --chart-1: 210 100% 62%;
  --chart-2: 160 84% 45%;
  --chart-3: 200 98% 45%;
  --chart-4: 38 92% 55%;
  --chart-5: 142 76% 42%;

  --shadow-2xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.20);
  --shadow-xs: 0px 1px 3px 0px hsl(0 0% 0% / 0.25);
  --shadow-sm: 0px 2px 4px -1px hsl(0 0% 0% / 0.22), 0px 1px 2px -1px hsl(0 0% 0% / 0.25);
  --shadow: 0px 4px 6px -1px hsl(0 0% 0% / 0.25), 0px 2px 4px -2px hsl(0 0% 0% / 0.22);
  --shadow-md: 0px 6px 12px -2px hsl(0 0% 0% / 0.28), 0px 3px 6px -3px hsl(0 0% 0% / 0.25);
  --shadow-lg: 0px 12px 24px -4px hsl(0 0% 0% / 0.32), 0px 6px 12px -6px hsl(0 0% 0% / 0.25);
  --shadow-xl: 0px 20px 40px -8px hsl(0 0% 0% / 0.38), 0px 10px 20px -10px hsl(0 0% 0% / 0.28);
  --shadow-2xl: 0px 32px 64px -12px hsl(0 0% 0% / 0.45);

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }
}

shared/schema.ts.content:
import { sql } from 'drizzle-orm';
import {
  index,
  jsonb,
  pgTable,
  timestamp,
  varchar,
  text,
  primaryKey,
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Session storage table for Replit Auth
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User roles: customer (layperson), researcher, admin
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  role: varchar("role", { length: 20 }).notNull().default('customer'), // customer, researcher, admin
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const upsertUserSchema = createInsertSchema(users).pick({
  id: true,
  email: true,
  firstName: true,
  lastName: true,
  profileImageUrl: true,
});

export type UpsertUser = z.infer<typeof upsertUserSchema>;
export type User = typeof users.$inferSelect;

// Admin-created tags for categorization
export const tags = pgTable("tags", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: varchar("name", { length: 50 }).notNull().unique(),
  description: text("description"),
  createdAt: timestamp("created_at").defaultNow(),
  createdBy: varchar("created_by").references(() => users.id),
});

export const insertTagSchema = createInsertSchema(tags).omit({
  id: true,
  createdAt: true,
});

export type InsertTag = z.infer<typeof insertTagSchema>;
export type Tag = typeof tags.$inferSelect;

// Infographics with multi-section structure
// Section A: Overview (title, sources, statistics, conclusions)
// Section B: Methods (methodology, participants, technical details)
// Section C: Solutions (3-5 pages of actionable steps for laypeople)
export const infographics = pgTable("infographics", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  researcherId: varchar("researcher_id").notNull().references(() => users.id),
  status: varchar("status", { length: 20 }).notNull().default('pending'), // pending, approved, rejected

  // Infographic content sections (stored as JSON)
  sectionA: jsonb("section_a").notNull(), // { title, summary, statistics, sources, conclusions }
  sectionB: jsonb("section_b").notNull(), // { methodology, participants, technicalTerms, studyDesign }
  sectionC: jsonb("section_c").notNull(), // Array of solution pages: [{ title, steps, badge }]

  // Metadata
  originalPaperText: text("original_paper_text"), // Store for reference
  researcherNotes: text("researcher_notes"), // Context from questionnaire

  // Admin review
  reviewedBy: varchar("reviewed_by").references(() => users.id),
  reviewedAt: timestamp("reviewed_at"),
  rejectionReason: text("rejection_reason"),

  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const insertInfographicSchema = createInsertSchema(infographics).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  reviewedAt: true,
});

export type InsertInfographic = z.infer<typeof insertInfographicSchema>;
export type Infographic = typeof infographics.$inferSelect;

// Many-to-many: infographics to tags
export const infographicTags = pgTable(
  "infographic_tags",
  {
    infographicId: varchar("infographic_id").notNull().references(() => infographics.id, { onDelete: 'cascade' }),
    tagId: varchar("tag_id").notNull().references(() => tags.id, { onDelete: 'cascade' }),
  },
  (table) => ({
    pk: primaryKey({ columns: [table.infographicId, table.tagId] }),
  })
);

export const insertInfographicTagSchema = createInsertSchema(infographicTags);
export type InsertInfographicTag = z.infer<typeof insertInfographicTagSchema>;
export type InfographicTag = typeof infographicTags.$inferSelect;

// User preferences for personalized feed
export const userTagPreferences = pgTable(
  "user_tag_preferences",
  {
    userId: varchar("user_id").notNull().references(() => users.id, { onDelete: 'cascade' }),
    tagId: varchar("tag_id").notNull().references(() => tags.id, { onDelete: 'cascade' }),
    addedAt: timestamp("added_at").defaultNow(),
  },
  (table) => ({
    pk: primaryKey({ columns: [table.userId, table.tagId] }),
  })
);

export const insertUserTagPreferenceSchema = createInsertSchema(userTagPreferences).omit({
  addedAt: true,
});

export type InsertUserTagPreference = z.infer<typeof insertUserTagPreferenceSchema>;
export type UserTagPreference = typeof userTagPreferences.$inferSelect;

